char os[] ="function setup()\n    print(\"setup\")\n    bgSet(2,1,1)\nend\nscrollx = 0\n\nfunction loop()\n    mouseX = getMouseX()\n    mouseY = getMouseY()\n    if (btn(6) == 1) then\n        bgSet((mouseX+(scrollx))%(256*2)/8, mouseY/8, 1)\n    end\n    --print(scrollx % 256)\n    if(btn(2) == 1) then\n        scrollx = scrollx + 1\n        setScrollX(scrollx)\n    end\n    local key = getCharPressed()\n    if(key ~= 0) then\n        print(string.char(key))\n    end\n    --\nend\n";
